import React, { Component } from 'react';
import './App.css';

class App extends Component{
  constructor(){
    super();

    this.state = {
      monsters : [
        {
          name : 'Frankenstein'
        },
        {
          name : 'Dracula'
        },
        {
          name : 'Zombie'
        }
      ]
    }
  }

  componentDidMount(){
    fetch("https://jsonplaceholder.typicode.com/users")
    .then(response => response.json())
    .then(users => console.log(users))
  }

  render(){
    return(
      <div className="App">
        {
          this.state.monsters.map(monster => <h1>{monster.name}</h1>)    // map iterates over all the elements ..so the function is ran on every elements in the arr
        }
      </div>
    )
  }

}

export default App;
